import React, { Component } from "react";
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, SafeAreaView, TouchableOpacity, View, ScrollView} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import * as ScreenOrientation from 'expo-screen-orientation';
import { Button, Input } from 'react-native-elements';
import styles from './Styles';


// Boceto 4: Pantalla principal del administrador
// Para la navegacion, reemplazar adminMenu en navigation

// Cambiar orientación de la pantalla
async function changeScreenOrientation() {
  await ScreenOrientation.lockAsync(ScreenOrientation.OrientationLock.LANDSCAPE_RIGHT);
}


function AddStockTask({navigation}) {
  changeScreenOrientation();

  const [error, onFillMessage] = React.useState(null);

  return (
    <View style={styles.container}>
      
      {/* Accessibility rol  */}
      <SafeAreaView style={styles.banner}>
        <Text style={styles.headerText} value="PANTALLA PRINCIPAL ADMIN" accessibilityRole="header">PANTALLA PRINCIPAL ADMIN</Text>
      </SafeAreaView> 

      <ScrollView style={styles.scrollView}>
      <View style={styles.goBackView}>
        {/* Volver a la pantalla anterior */}
        <TouchableOpacity onPress={() => this.props.navigation.navigate('')}>
          <Button
          title="Volver"
          accessibilityLabel="Submenú tareas"
          accessibilityRole="button"
          accessibilityHint="Vuelve al submenú de tareas"
          />
        </TouchableOpacity>
      </View>

      <View style={styles.fixToText}>

        {/* Poner titulo a una tarea */}
        <View style={styles.Input}>
          <TouchableOpacity style={styles.Input}>
            <Input
                label='Título tarea'
                placeholder='Introduce una tarea tarea'
            />
          </TouchableOpacity>
        </View>

        {/* Ir al menu de tareas (Boceto 11)*/}
        <View style={styles.enterButtonView}>
          <TouchableOpacity style={styles.enterButtonTouch} onPress={() => this.props.navigation.navigate('') }>
            <Button
              title="Tareas"
              accessibilityLabel="Gestionar las tareas"
              accessibilityRole="button"
              accessibilityHint="Ir al menu de tareas"
            />
          </TouchableOpacity>
        </View>
      </View>

      <View style={styles.enterButtonView}>
        {/* Ir al menu de docs (Boceto 10) */}
        <TouchableOpacity style={styles.enterButtonTouch} onPress={() => this.props.navigation.navigate('') }>
          <Button
            title="Documentos"
            accessibilityLabel="Menu de documentos"
            accessibilityRole="button"
            accessibilityHint="Ir al menu de documentos"
          />
        </TouchableOpacity>
      </View>
      </ScrollView>
    </View>

  )
}

export default AddStockTask;
