import React, { Component } from "react";
import { StatusBar } from 'expo-status-bar';
import { Text, SafeAreaView, TouchableOpacity, View, Image } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import * as ScreenOrientation from 'expo-screen-orientation';
import styles from './Styles';

// Boceto 4: Pantalla principal del administrador

// Cambiar orientación de la pantalla
async function changeScreenOrientation() {
  await ScreenOrientation.lockAsync(ScreenOrientation.OrientationLock.LANDSCAPE_RIGHT);
}

function adminMenu({ navigation }) {
  changeScreenOrientation();
  return (
    <View style={styles.container}>
      <View style={styles.goBackView}>

        {/* Volver a la pantalla anterior */}
        <TouchableOpacity onPress={() => navigation.navigate('adminMenu')}>
          <Text style={styles.goBackText}>Volver</Text>
        </TouchableOpacity>
      </View>
      
      {/* Acceder a los usuarios */}
      <TouchableOpacity style={styles.taskButtonTouch} onPress={() => navigation.navigate('adminMenu')}>
        <Text style={styles.taskButtonText}>Usuarios</Text>
      </TouchableOpacity>
      {/* Acceder a las tareas */}
      <TouchableOpacity style={styles.taskButtonTouch} onPress={() => navigation.navigate('adminMenu')}>
        <Text style={styles.taskButtonText}>Tareas</Text>
      </TouchableOpacity>
      {/* Acceder a los documentos */}
      <TouchableOpacity style={styles.taskButtonTouch} onPress={() => navigation.navigate('adminMenu')}>
        <Text style={styles.taskButtonText}>Añadir comanda stock</Text>
      </TouchableOpacity>

    </View>
  );
}

export default adminMenu;
