import React, { Component } from "react";
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Button, Text, SafeAreaView, TouchableOpacity, View, Image, Alert} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import * as ScreenOrientation from 'expo-screen-orientation';
import styles from './Styles';


// Boceto 4: Pantalla principal del administrador

// Cambiar orientación de la pantalla
async function changeScreenOrientation() {
  await ScreenOrientation.lockAsync(ScreenOrientation.OrientationLock.LANDSCAPE_RIGHT);
}

function adminMenu({navigation}) {
  changeScreenOrientation();
  return (
    <View style={styles.container}>
      <View style={styles.goBackView}>
        {/* Volver a la pantalla anterior */}
        <TouchableOpacity onPress={() => navigation.navigate('')}>
          <Button
          title="Salir"
          accessibilityLabel="Volver al inicio de sesión"
          />
        </TouchableOpacity>
      </View>

      <View style={styles.enterButtonView}>

        {/* Ir al menu de estudiantes (Boceto 5) */}
        <TouchableOpacity style={styles.enterButtonTouch} onPress={() => this.props.navigation.navigate('') }>
          <Button
            title="Usuarios"
            accessibilityLabel="Volver al inicio de sesión"
          />
        </TouchableOpacity>

        {/* Ir al menu de tareas (Boceto 11)*/}
        <TouchableOpacity style={styles.enterButtonTouch} onPress={() => this.props.navigation.navigate('') }>
          <Button
            title="Usuarios"
            accessibilityLabel="Volver al inicio de sesión"
          />
        </TouchableOpacity>

        {/* Ir al menu de docs () */}
      </View>
    </View>

  )
}

export default adminMenu;

